import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import App from './App';
import * as serviceWorker from './serviceWorker';

//ReactDOM.render(<PortfolioButton />);

// If you want your app to work offline and load faster, you can change
// unregister() to register() below. Note this comes with some pitfalls.
// Learn more about service workers: https://bit.ly/CRA-PWA
serviceWorker.unregister();

class PortfolioButton extends React.Component {
    renderSquare(i) {
        return <Square value={i} />;
    }

    render() {
        return (
            <div>
                {this.renderSquare(2)}
            </div>
        );
    }
}

class Square extends React.Component {
    constructor(props) {
        console.log("hi");
        super(props);
        this.state = {hover: false};
    }

    toggleHover() {
        console.log("hey");
        this.setState({hover: !this.state.hover});
    }

    render(i) {
        var buttonUnhovered = {
            display: "block",
            paddingLeft: "1.1em",
            paddingRight: "1.1em",
            borderRight: "1px solid #555",
            background: "-webkit-gradient(linear, left top, left bottom, from(#4dd151), to(#195c12))",
        };

        var buttonHovered = {
            display: "block",
            cursor: "pointer",
            paddingLeft: "1.1em",
            paddingRight: "1.1em",
            backgroundColor: "#414143",
            color: "#FFF",
            borderRight: "1px solid #555"
        };

        var buttonStyle;

        if (this.state.hover) {
            buttonStyle = buttonHovered
        } else {
            buttonStyle = buttonUnhovered
        }

        return (
            <li style={{float: "left", textAlign: "center", fontSize: "calc(80% + 1.7vh)"}}> 
                <a onMouseEnter={this.toggleHover} 
                    onMouseLeave={this.toggleHover} 
                    onClick={this.goTo}
                    style={buttonStyle}
                    >{this.props.text}</a>
            </li>
        );
    }
}

class Portfolio extends React.Component {
    render(){

        const titles = ["hi", "test"];

        return(
            <div>
                <div>
                    <h1>{titles[0]}</h1>
                </div>
                <PortfolioButton />
            </div>
        );

    }
}





