import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import App from './App';
import * as serviceWorker from './serviceWorker';
import sun from "./Painting_Sun.jpg";

// If you want your app to work offline and load faster, you can change
// unregister() to register() below. Note this comes with some pitfalls.
// Learn more about service workers: https://bit.ly/CRA-PWA
serviceWorker.unregister();



class PortfolioButton extends React.Component {
    constructor(props) {
        console.log("hi, button");
        super(props);
        this.state = {hover: false};
        this.toggleHover = this.toggleHover.bind(this);
    }

    toggleHover() {
        console.log("hey");
        this.setState({hover : !this.state.hover});
    }

    render(i) {
        var unhoveredStyle = {
            padding: "1vw",
            display: "inline-block",
            width: "30%",
            fontFamily: "monospace"
        };

        var hoveredStyle = {
            padding: "1vw",
            display: "inline-block",
            width: "30%",
            backgroundColor: "grey",
            fontFamily: "monospace",
            cursor: "pointer"
        };

        var styleUsed;

        if (this.state.hover) {
            styleUsed = hoveredStyle
        } else {
            styleUsed = unhoveredStyle
        }


        return (
            <div    onClick={this.toggleHover} 
                    style={styleUsed} 
                    onMouseEnter={this.toggleHover} 
                    onMouseLeave={this.toggleHover}>
                <TitleIcon title={this.props.title}></TitleIcon>
                <img src={sun} style={{ width: "100%",
                                                    height: "26vw",
                                                    objectFit: "cover"}}></img>
                <UnderIcon desc={this.props.desc}></UnderIcon>
            </div>
        );
    }
}

class TitleIcon extends React.Component {
    render(){
        return(
            <div>aa</div>
        );
    }
}

class UnderIcon extends React.Component {
    render(){
        return(
            <div>bb</div>
        );
    }
}

class Portfolio extends React.Component {

    renderPortfolioButton(number){
        return <PortfolioButton title={this.titles[number]} underTitle={underTitles[number]} />
    }

    render(){



        return(
            <div>
                <div>
                    <h1>{titles[0]}</h1>
                </div>
                {this.renderPortfolioButton(0)}
                {this.renderPortfolioButton(1)}
                {this.renderPortfolioButton(2)}
            </div>
        );

    }
}





  // ========================================

